import React, { useState, useEffect, useRef } from 'react';
import './Simulation.css';

class Product {
constructor(x, y, cost, ID, groupID, attractive) {
  this.x = x;
  this.y = y;
  this.cost = cost;               // цена товара
  this.ID = ID;
  this.groupID = groupID;         // номер отдела
  this.size = 6;
  this.attractive = attractive;
  if (attractive > 0)
    this.color = '#30b705';
  else this.color = '#7FFFD4';  
  }
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
}

class Stall {
  constructor(x, y, width, height, ID) {
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.ID = ID;
    this.color = '#1E90FF';
    this.products = [];
  }
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.fillRect(this.x, this.y, this.width, this.height);
    ctx.fill();
  }
}

class Human {
  constructor(x, y, speed) {
    this.x = x;
    this.y = y;
    this.speed = speed;
    this.targetX = 0;
    this.targetY = 0;
    this.size = 6;
    this.direction = Math.PI / 2;
  }

  move(width, height, stalls, stallsWidth, stallsHeight) {
    const dx = this.targetX - this.x;
    const dy = this.targetY - this.y;
    const distance = Math.sqrt(dx * dx + dy * dy);

    if (distance > 0) {  // Avoid division by zero if already at target
      const angle = Math.atan2(dy, dx); // Calculate the angle to the target
      this.direction += angle;
      this.x += this.speed * Math.cos(angle);
      this.y += this.speed * Math.sin(angle);

      // Keep within bounds (optional but good practice)
      if (this.x < 0) this.x = 0;
      if (this.x > width) this.x = width;
      if (this.y < 0) this.y = 0;
      if (this.y > height) this.y = height;

    } else {
        // Already at the target, stop moving or do something else
        return; // Or set some flag to indicate arrival
    }
    this.avoidObstacles(stalls, stallsWidth, stallsHeight);
  }

  avoidObstacles(stalls, stallsWidth, stallsHeight) {
      const nearestStall = this.findNearestStall(stalls, stallsHeight);
      if (nearestStall == null) return;
    if (calculateDistance(this.x, this.y, nearestStall.x, nearestStall.y) < this.size || 
    (this.x - (nearestStall.x + stallsWidth / 2) < this.size + stallsWidth / 2 && this.y > nearestStall.y - this.size && this.y < nearestStall.y + this.size + stallsHeight)) {
      this.direction += 0.1; // Small adjustment to avoid the stall
    }
  }

  findNearestStall(stalls, stallsHeight) {
    if (!stalls || stalls.length === 0)
      return null
    let nearestStall = null;
    let shortestDistance = Infinity;

    for (const stall of stalls) {
      const distance = Math.min(calculateDistance(this.x, this.y, stall.x, stall.y), calculateDistance(this.x, this.y, stall.x, stall.y + stallsHeight));

      if (distance < shortestDistance) {
        shortestDistance = distance;
        nearestStall = stall;
      }
    }
    
    return nearestStall;
  }


  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();

    // Глаз для направления
    ctx.fillStyle = 'white';
    ctx.beginPath();
    ctx.arc(
      this.x + Math.cos(this.direction) * this.size / 2,
      this.y + Math.sin(this.direction) * this.size / 2,
      this.size / 3,
      0,
      Math.PI * 2
    );
    ctx.fill();
  }
}

class Customer extends Human {
  constructor(x, y, speed, productsAmountToBuy, productsCount, money) {
    super(x, y, speed);
    this.money = money;
    this.direction = Math.PI / 2;
    this.size = 6;
    this.color = '#FF8C00';
    this.productsAmount = productsAmountToBuy;
    this.productsToBuy = []
    this.checkedShelvesIDs = []
    for (let i = 0; i < productsAmountToBuy; i++) {
      this.productsToBuy.push(Math.floor(Math.random() * (productsCount - 1) + 1.1));
    }
  }
  avoidObstacles(stalls, stallsWidth, stallsHeight) {
    const nearestStall = this.findNearestStall(stalls, stallsHeight);
    if (nearestStall == null) return;
    if ((calculateDistance(this.x, this.y, nearestStall.x, nearestStall.y) < this.size || 
    (this.x - (nearestStall.x + stallsWidth / 2) < this.size + stallsWidth / 2 && this.y > nearestStall.y - this.size && this.y < nearestStall.y + this.size + stallsHeight)) && 
    this.checkedShelvesIDs.includes(nearestStall.ID)) {
      this.direction += 0.1; // Small adjustment to avoid the stall
    }
  }

  findNearestStall(stalls, stallsHeight) {
    if (!stalls || stalls.length === 0)
      return null
    let nearestStall = null;
    let shortestDistance = Infinity;

    for (const stall of stalls) {
      const distance = Math.min(calculateDistance(this.x, this.y, stall.x, stall.y), calculateDistance(this.x, this.y, stall.x, stall.y + stallsHeight));

      if (distance < shortestDistance && !this.checkedShelvesIDs.includes(stall.ID)) {
        shortestDistance = distance;
        nearestStall = stall;
      }
    }
    if (nearestStall == null) return;

    this.targetX = nearestStall.x;
    
    if (calculateDistance(this.x, this.y, nearestStall.x, nearestStall.y) < calculateDistance(this.x, this.y, nearestStall.x, nearestStall.y + stallsHeight))
      this.targetY = nearestStall.y;
    else this.targetY = nearestStall.y + stallsHeight;

    return nearestStall;
  }

  move(width, height, stalls, stallsWidth, stallsHeight) {
    const dx = this.targetX - this.x;
    const dy = this.targetY - this.y;
    const distance = Math.sqrt(dx * dx + dy * dy);

    if (distance > 0) {  // Avoid division by zero if already at target
      const angle = Math.atan2(dy, dx); // Calculate the angle to the target

      this.x += this.speed * Math.cos(angle);
      this.y += this.speed * Math.sin(angle);

      // Keep within bounds (optional but good practice)
      if (this.x < 0) this.x = 0;
      if (this.x > width) this.x = width;
      if (this.y < 0) this.y = 0;
      if (this.y > height) this.y = height;

    } else {
        // Already at the target, stop moving or do something else
        return; // Or set some flag to indicate arrival
    }
    this.avoidObstacles(stalls, stallsWidth, stallsHeight);
  }

  checkStall(stall) {

  }
}

function calculateDistance(x1, y1, x2, y2) {
  return Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
}

const Simulation = () => {
  const canvasRef = useRef(null);
  const stallsRef = useRef([]);
  const productsRef = useRef([]);
  const customersRef = useRef([]);
  const [params, setParams] = useState({
    stallsCount: 5,
    width: 1200,
    height: 675,
    stallsWidth: 20,
    stallsHeight: 675 - 250,
    isRunning: true,
    customersCount: 5,
    speed: 1
  });
  const [visibleCount, setVisibleCount] = useState(0);

  // Инициализация
  const initSimulation = () => {
    const newStalls = [];
    const newProducts = [];
    const newCustomers = [];
    const distBetweenStalls = (params.width - params.stallsCount * params.stallsWidth) / (params.stallsCount - 1);
    for (let i = 0; i < params.stallsCount; i++) {
      const newStall = new Stall(
        i * params.stallsWidth + i * distBetweenStalls,
        50,
        params.stallsWidth,
        params.stallsHeight,
        i + 1);
      newStalls.push(newStall);
      for (let j = 0; j < params.stallsHeight / 12; j++) {
        const newProduct = new Product(
          i * params.stallsWidth + i * distBetweenStalls + params.stallsWidth / 2,
          50 + j * 12 + 3,
          Math.random() * 1000 + 10,
          j + 1,
          i + 1,
          Math.random() <= 0.25? 0 : Math.random() * 90)
        newStall.products.push(newProduct);
        newProducts.push(newProduct);
      }
    }
    for (let i = 0; i < params.customersCount; i++) {
      newCustomers.push(new Customer(Math.random() * params.width, 
      params.height - 6, 
      params.speed, 
      Math.random() * 9 + 1,
      newProducts.length,
      Math.random()))
    }
    customersRef.current = newCustomers;
    stallsRef.current = newStalls;
    productsRef.current = newProducts;
    setVisibleCount(productsRef.current.length);
  };

  const handleParamChange = (e) => {
    const { name, value } = e.target;
    setParams(prev => ({
      ...prev,
      [name]: parseFloat(value)
    }));
  };

  const toggleSimulation = () => {
    setParams(prev => ({
      ...prev,
      isRunning: !prev.isRunning
    }));
  };

  useEffect(() => {
    initSimulation();
  }, [params.stallsCount]);

  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      customersRef.current.forEach(c => {
        c.findNearestStall(stallsRef.current, params.stallsHeight);
        c.move(canvas.width, canvas.height, stallsRef.current, params.stallsWidth, params.stallsHeight);
        c.draw(ctx);
      });
      

      stallsRef.current.forEach(p => {
        p.draw(ctx);
      });

      productsRef.current.forEach(p => {
        p.draw(ctx);
      });
      
      // herbivoresRef.current = herbivoresRef.current.filter(h => h.energy > 0);
      // setVisibleCount(herbivoresRef.current.length);
      
      if (params.isRunning) {
        animationFrameId = requestAnimationFrame(animate);
      }
    };
    
    if (params.isRunning) {
      animationFrameId = requestAnimationFrame(animate);
    } else {
      // При паузе рисуем текущее состояние
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      stallsRef.current.forEach(p => p.draw(ctx));
      productsRef.current.forEach(p => p.draw(ctx));
      customersRef.current.forEach(c => c.draw(ctx));
    }

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [params.isRunning]);

  return (
    <div className="simulation-container">
      <div className="controls">
        <h2>Управление симуляцией</h2>
        
        <div className="control-group">
          <label>
            Количество отделов товаров:
            <input
              type="range"
              name="stallsCount"
              min="1"
              max="10"
              value={params.stallsCount}
              onChange={handleParamChange}
            />
            {params.stallsCount}
          </label>
        </div>
        
        <div className="control-group">
          <label>
            Количество покупателей:
            <input
              type="range"
              name="customersCount"
              min="1"
              max="30"
              value={params.customersCount}
              onChange={handleParamChange}
            />
            {params.customersCount}
          </label>
        </div>
        
        <div className="control-group">
          <label>
            Скорость:
            <input
              type="range"
              name="speed"
              min="0.5"
              max="5"
              step="0.1"
              value={params.speed}
              onChange={handleParamChange}
            />
            {params.speed.toFixed(1)} 
          </label>
        </div>
        
        <div className="buttons">
          <button onClick={initSimulation}>Сбросить</button>
          <button onClick={toggleSimulation}>
            {params.isRunning ? 'Пауза' : 'Старт'}
          </button>
        </div>
        
        <div className="info">
          Товаров: {visibleCount}
        </div>
      </div>
      
      <div className="simulation-area">
        <canvas
          ref={canvasRef}
          width={params.width}
          height={params.height}
        />
      </div>
    </div>
  );
};

export default Simulation;
