import React, { useState, useEffect, useRef } from 'react';
import './Simulation.css';

class Product {
constructor(x, y, cost, groupID) {
  this.x = x;
  this.y = y;
  this.cost = cost;               // цена товара
  this.groupID = groupID;         // номер отдела
  this.size = 6;
  this.color = '#30b705';
  }
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
}

class AttractiveProduct extends Product {
  constructor(x, y, cost, attractive) {
    super(x, y, cost, 0)
    this.attractive = attractive
    this.color = '#7FFFD4';
  }
}

class ProductsGroup {
  constructor(x, y, width, height, ID) {
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.ID = ID;
    this.color = '#1E90FF';
  }
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.fillRect(this.x, this.y, this.width, this.height);
    ctx.fill();
  }
}

class Stall extends ProductsGroup {
  constructor(x, y, width, height) {
    super(x, y, width, height)
    this.color = '#7B68EE';
  }
}

class Herbivore {
  constructor(x, y, speed) {
    this.x = x;
    this.y = y;
    this.speed = speed;
    this.direction = Math.random() * Math.PI * 2;
    this.energy = Math.random() * 50 + 50;
    this.size = 6;
    this.color = '#3498db';
  }

  move(width, height) {
    if (Math.random() < 0.1) {
      this.direction += (Math.random() - 0.5) * Math.PI / 4;
    }

    this.x += Math.cos(this.direction) * this.speed;
    this.y += Math.sin(this.direction) * this.speed;

    // Обработка границ
    if (this.x < this.size) {
      this.x = this.size;
      this.direction = Math.PI - this.direction;
    }
    if (this.x > width - this.size) {
      this.x = width - this.size;
      this.direction = Math.PI - this.direction;
    }
    if (this.y < this.size) {
      this.y = this.size;
      this.direction = -this.direction;
    }
    if (this.y > height - this.size) {
      this.y = height - this.size;
      this.direction = -this.direction;
    }

    this.energy -= 0.05;
  }

  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
    
    // Глаз для направления
    ctx.fillStyle = 'white';
    ctx.beginPath();
    ctx.arc(
      this.x + Math.cos(this.direction) * this.size/2,
      this.y + Math.sin(this.direction) * this.size/2,
      this.size/3, 
      0, 
      Math.PI * 2
    );
    ctx.fill();
  }
}

const Simulation = () => {
  const canvasRef = useRef(null);
  const productsGroupsRef = useRef([]);
  const [params, setParams] = useState({
    productsGroupsCount: 5,
    width: 1200,
    height: 675,
    isRunning: true
  });
  const [visibleCount, setVisibleCount] = useState(0);
// new Product(
//         Math.random() * (params.width - 20) + 10,
//         Math.random() * (params.height - 20) + 10,
//         Math.random() * 1000 + 10,
//         Math.floor(Math.random() * 4 + 1)
//       )
  // Инициализация
  const initSimulation = () => {
    const newProductsGroups = [];
    const productsGroupsWidth = 20;
    const productsGroupsHeight = params.height - 250;
    const widthBetweenProductsGroups = (params.width - params.productsGroupsCount * productsGroupsWidth) / (params.productsGroupsCount - 1);
    for (let i = 0; i < params.productsGroupsCount; i++) {
      newProductsGroups.push(new ProductsGroup(
        i * productsGroupsWidth + i * widthBetweenProductsGroups,
        250,
        productsGroupsWidth,
        productsGroupsHeight,
        i + 1
      ))
    }
    productsGroupsRef.current = newProductsGroups;
    setVisibleCount(newProductsGroups.length);
  };

  const handleParamChange = (e) => {
    const { name, value } = e.target;
    setParams(prev => ({
      ...prev,
      [name]: parseFloat(value)
    }));
  };

  const toggleSimulation = () => {
    setParams(prev => ({
      ...prev,
      isRunning: !prev.isRunning
    }));
  };

  useEffect(() => {
    initSimulation();
  }, [params.productsCount]);

  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Обновляем позиции
      // herbivoresRef.current.forEach(h => {
      //   h.move(canvas.width, canvas.height);
      // });
      
      // Отрисовываем всех травоядных
      productsGroupsRef.current.forEach(p => {
        p.draw(ctx);
      });
      
      // Фильтруем умерших
      // herbivoresRef.current = herbivoresRef.current.filter(h => h.energy > 0);
      // setVisibleCount(herbivoresRef.current.length);
      
      if (params.isRunning) {
        animationFrameId = requestAnimationFrame(animate);
      }
    };
    
    if (params.isRunning) {
      animationFrameId = requestAnimationFrame(animate);
    } else {
      // При паузе рисуем текущее состояние
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      productsGroupsRef.current.forEach(p => p.draw(ctx));
    }

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [params.isRunning]);

  return (
    <div className="simulation-container">
      <div className="controls">
        <h2>Управление симуляцией</h2>
        
        <div className="control-group">
          <label>
            Количество товаров:
            <input
              type="range"
              name="productsCount"
              min="1"
              max="30"
              value={params.productsCount}
              onChange={handleParamChange}
            />
            {params.productsCount}
          </label>
        </div>
        
        <div className="control-group">
          <label>
            {/* Скорость:
            <input
              type="range"
              name="herbivoreSpeed"
              min="0.5"
              max="5"
              step="0.1"
              value={params.herbivoreSpeed}
              onChange={handleParamChange}
            />
            {params.herbivoreSpeed.toFixed(1)} */}
          </label>
        </div>
        
        <div className="buttons">
          <button onClick={initSimulation}>Сбросить</button>
          <button onClick={toggleSimulation}>
            {params.isRunning ? 'Пауза' : 'Старт'}
          </button>
        </div>
        
        <div className="info">
          Товаров: {visibleCount}
        </div>
      </div>
      
      <div className="simulation-area">
        <canvas
          ref={canvasRef}
          width={params.width}
          height={params.height}
        />
      </div>
    </div>
  );
};

export default Simulation;
