import React, { useState, useEffect, useRef } from 'react';
import './Simulation.css';

class Product {
constructor(x, y, cost, ID, groupID, attractive) {
  this.x = x;
  this.y = y;
  this.cost = cost;               // цена товара
  this.ID = ID;
  this.groupID = groupID;         // номер отдела
  this.size = 6;
  this.attractive = attractive;
  if (attractive > 0)
    this.color = '#30b705';
  else this.color = '#7FFFD4';  
  }
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
}

class Stall {
  constructor(x, y, width, height, ID) {
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.ID = ID;
    this.color = '#1E90FF';
    this.products = [];
  }
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.fillRect(this.x, this.y, this.width, this.height);
    ctx.fill();
  }
}

class Human {
  constructor(x, y, speed) {
    this.x = x;
    this.y = y;
    this.speed = speed;
    this.targetX = 0;
    this.targetY = 0;
    this.size = 6;
  }
  move(width, height, targetX, targetY) {
    
  }

  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
    
    // Глаз для направления
    ctx.fillStyle = 'white';
    ctx.beginPath();
    ctx.arc(
      this.x + Math.cos(this.direction) * this.size/2,
      this.y + Math.sin(this.direction) * this.size/2,
      this.size/3, 
      0, 
      Math.PI * 2
    );
    ctx.fill();
  }
}

class Customer extends Human {
  constructor(x, y, speed, productsAmountToBuy, productsCount) {
    super(x, y, speed);
    this.direction = Math.PI / 2;
    this.size = 6;
    this.color = '#FF8C00';
    this.productsAmount = productsAmountToBuy;
    this.productsToBuy = []
    this.checkedShelvesIDs = []
    for (let i = 0; i < productsAmountToBuy; i++) {
      this.productsToBuy.push(Math.floor(Math.random() * (productsCount - 1) + 1.1));
    }
  }
  findNearestStall(stalls) {
    if (!stalls || stalls.length === 0)
      return null
    let nearestStall = null;
    let shortestDistance = Infinity;

    for (const stall of stalls) {
      const distance = calculateDistance(this.x, this.y, stall.x, stall.y);

      if (distance < shortestDistance) {
        shortestDistance = distance;
        nearestStall = stall;
      }
    }
    
    return nearestStall;
  }
}

function calculateDistance(x1, y1, x2, y2) {
  return Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
}

const Simulation = () => {
  const canvasRef = useRef(null);
  const stallsRef = useRef([]);
  const productsRef = useRef([]);
  const [params, setParams] = useState({
    stallsCount: 5,
    width: 1200,
    height: 675,
    isRunning: true,
    customersCount: 5,
    speed: 1
  });
  const [visibleCount, setVisibleCount] = useState(0);

  // Инициализация
  const initSimulation = () => {
    const newStalls = [];
    const newProducts = [];
    const stallWidth = 20;
    const stallHeight = params.height - 250;
    const distBetweenStalls = (params.width - params.stallsCount * stallWidth) / (params.stallsCount - 1);
    for (let i = 0; i < params.stallsCount; i++) {
      const newStall = new Stall(
        i * stallWidth + i * distBetweenStalls,
        50,
        stallWidth,
        stallHeight,
        i + 1);
      newStalls.push(newStall);
      for (let j = 0; j < stallHeight / 12; j++) {
        const newProduct = new Product(
          i * stallWidth + i * distBetweenStalls + stallWidth / 2,
          50 + j * 12 + 3,
          Math.random() * 1000 + 10,
          j + 1,
          i + 1,
          Math.random() <= 0.25? 0 : Math.random() * 90)
        newStall.products.push(newProduct);
        newProducts.push(newProduct);
      }
    }
    stallsRef.current = newStalls;
    productsRef.current = newProducts;
    setVisibleCount(productsRef.current.length);
  };

  const handleParamChange = (e) => {
    const { name, value } = e.target;
    setParams(prev => ({
      ...prev,
      [name]: parseFloat(value)
    }));
  };

  const toggleSimulation = () => {
    setParams(prev => ({
      ...prev,
      isRunning: !prev.isRunning
    }));
  };

  useEffect(() => {
    initSimulation();
  }, [params.stallsCount]);

  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // herbivoresRef.current.forEach(h => {
      //   h.move(canvas.width, canvas.height);
      // });
      

      stallsRef.current.forEach(p => {
        p.draw(ctx);
      });

      productsRef.current.forEach(p => {
        p.draw(ctx);
      });
      
      // herbivoresRef.current = herbivoresRef.current.filter(h => h.energy > 0);
      // setVisibleCount(herbivoresRef.current.length);
      
      if (params.isRunning) {
        animationFrameId = requestAnimationFrame(animate);
      }
    };
    
    if (params.isRunning) {
      animationFrameId = requestAnimationFrame(animate);
    } else {
      // При паузе рисуем текущее состояние
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      stallsRef.current.forEach(p => p.draw(ctx));
      productsRef.current.forEach(p => p.draw(ctx));
    }

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [params.isRunning]);

  return (
    <div className="simulation-container">
      <div className="controls">
        <h2>Управление симуляцией</h2>
        
        <div className="control-group">
          <label>
            Количество отделов товаров:
            <input
              type="range"
              name="stallsCount"
              min="1"
              max="10"
              value={params.stallsCount}
              onChange={handleParamChange}
            />
            {params.stallsCount}
          </label>
        </div>
        
        <div className="control-group">
          <label>
            Количество покупателей:
            <input
              type="range"
              name="customersCount"
              min="1"
              max="30"
              value={params.customersCountCount}
              onChange={handleParamChange}
            />
            {params.stallsCount}
          </label>
        </div>
        
        <div className="control-group">
          <label>
            Скорость:
            <input
              type="range"
              name="speed"
              min="0.5"
              max="5"
              step="0.1"
              value={params.speed}
              onChange={handleParamChange}
            />
            {params.speed.toFixed(1)} 
          </label>
        </div>
        
        <div className="buttons">
          <button onClick={initSimulation}>Сбросить</button>
          <button onClick={toggleSimulation}>
            {params.isRunning ? 'Пауза' : 'Старт'}
          </button>
        </div>
        
        <div className="info">
          Товаров: {visibleCount}
        </div>
      </div>
      
      <div className="simulation-area">
        <canvas
          ref={canvasRef}
          width={params.width}
          height={params.height}
        />
      </div>
    </div>
  );
};

export default Simulation;
