import React, { useState, useEffect, useRef } from 'react';
import './Simulation.css';

class Product {
constructor(x, y, cost, groupID, attractive) {
  this.x = x;
  this.y = y;
  this.cost = cost;               // цена товара
  this.groupID = groupID;         // номер отдела
  this.size = 6;
  this.attractive = attractive;
  if (attractive > 0)
    this.color = '#30b705';
  else this.color = '#7FFFD4';  
  }
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
}

class Stall {
  constructor(x, y, width, height, ID) {
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.ID = ID;
    this.color = '#1E90FF';
  }
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.fillRect(this.x, this.y, this.width, this.height);
    ctx.fill();
  }
}

