import React, { useState, useEffect, useRef } from 'react';
import './Simulation.css';

class Product {
  constructor(x, y, cost, ID, stall, attractive) {
    this.x = x;
    this.y = y;
    this.cost = cost;
    this.ID = ID;
    this.stall = stall;
    this.size = 6;
    this.attractive = attractive;
    if (attractive > 0)
      this.color = '#30b705';
    else this.color = '#7FFFD4';
    this.originalCost = cost;
  }
  
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
  
  checkNearby() {
    const productsNearby = [];
    const productIndex = this.stall.products.indexOf(this);
    if (productIndex > 0) productsNearby.push(this.stall.products[productIndex - 1]);
    if (productIndex < this.stall.products.length - 1) productsNearby.push(this.stall.products[productIndex + 1]);
    return productsNearby;
  }
}

class Stall {
  constructor(x, y, width, height, ID) {
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.ID = ID;
    this.color = '#1E90FF';
    this.products = [];
    this.restockTimer = 0;
  }
  
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.fillRect(this.x, this.y, this.width, this.height);
    ctx.fill();
  }
  
  calculateDistanceToStall(x, y) {
    return Math.min(
      calculateDistance(x, y, this.x, this.y),
      calculateDistance(x, y, this.x, this.y + this.height),
      calculateDistance(x, y, this.x + this.width, this.y),
      calculateDistance(x, y, this.x + this.width, this.y + this.height)
    );
  }
  
  restock(productsCount) {
    if (this.products.length < productsCount / 2) {
      this.restockTimer++;
      if (this.restockTimer > 100) {
        const newProduct = new Product(
          this.x + this.width / 2,
          this.y + this.height - (this.products.length * 12 + 3),
          Math.random() * 1000 + 10,
          Math.floor(Math.random() * 100) + 1,
          this,
          Math.random() <= 0.5 ? 0 : Math.random() * 90
        );
        this.products.push(newProduct);
        this.restockTimer = 0;
        return true;
      }
    }
    return false;
  }
}

class Cashier {
  constructor(x, y, width, height) {
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.color = '#FF6347';
    this.queue = [];
    this.processingTime = 0;
    this.totalRevenue = 0;
    this.customersProcessed = 0;
  }
  
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.fillRect(this.x, this.y, this.width, this.height);
    ctx.fill();
    
    // Draw queue
    ctx.fillStyle = 'rgba(255, 99, 71, 0.5)';
    for (let i = 0; i < this.queue.length; i++) {
      ctx.beginPath();
      ctx.arc(this.x - 20 - i * 20, this.y + this.height / 2, 6, 0, Math.PI * 2);
      ctx.fill();
    }
  }
  
  processCustomer() {
    if (this.queue.length > 0) {
      this.processingTime++;
      if (this.processingTime > 60) {
        const customer = this.queue.shift();
        let total = 0;
        customer.productsTaken.forEach(product => {
          total += product.cost;
        });
        this.totalRevenue += total;
        this.customersProcessed++;
        this.processingTime = 0;
        return customer;
      }
    }
    return null;
  }
}

class Human {
  constructor(x, y, speed) {
    this.x = x;
    this.y = y;
    this.speed = speed;
    this.targetX = x;
    this.targetY = y;
    this.size = 6;
    this.direction = Math.PI / 2;
    this.stop = false;
    this.color = '#FF8C00';
  }
  
  move(width, height, stalls, stallsWidth, stallsHeight, humans) {
    const dx = this.targetX - this.x;
    const dy = this.targetY - this.y;
    const distance = Math.sqrt(dx * dx + dy * dy);
    
    if (distance > 0 && !this.stop) {
      this.direction = Math.atan2(dy, dx);
      
      const avoidanceForce = this.avoidObstacles(stalls, stallsWidth, stallsHeight, humans);
      const combinedAngle = Math.atan2(dy + avoidanceForce[1], dx + avoidanceForce[0]);
      this.direction = combinedAngle;
      this.x += this.speed * Math.cos(this.direction);
      this.y += this.speed * Math.sin(this.direction);
      
      if (this.x < 0) this.x = 0;
      if (this.x > width) this.x = width;
      if (this.y < 0) this.y = 0;
      if (this.y > height) this.y = height;
    }
  }
  
  stopMove() {
    this.targetX = this.x;
    this.targetY = this.y;
    this.stop = true;
  }
  
  avoidObstacles(stalls, stallsWidth, stallsHeight, humans) {
    let avoidanceForceX = 0;
    let avoidanceForceY = 0;
    
    for (const otherHuman of humans) {
      if (otherHuman !== this && otherHuman !== null) {
        const distToHuman = calculateDistance(this.x, this.y, otherHuman.x, otherHuman.y);
        if (distToHuman < this.size * 3) {
          const forceMagnitude = (this.size * 3 - distToHuman) * 1;
          avoidanceForceX += forceMagnitude * (this.x - otherHuman.x) / distToHuman;
          avoidanceForceY += forceMagnitude * (this.y - otherHuman.y) / distToHuman;
        }
      }
    }
    
    const nearestStall = this.findNearestStall(stalls);
    if (nearestStall == null) return [0, 0];
    
    let distToStall = Infinity;
    if (this.y > nearestStall.y && this.y < nearestStall.y + stallsHeight) {
      distToStall = this.x > nearestStall.x ? this.x - nearestStall.x - stallsWidth : nearestStall.x - this.x;
      if (distToStall < this.size * 3) {
        const forceMagnitude = (this.size * 3 - distToStall) * 1;
        avoidanceForceX += forceMagnitude * (this.x - nearestStall.x) / distToStall;
      }
    } else {
      distToStall = nearestStall.calculateDistanceToStall(this.x, this.y);
      if (distToStall < this.size * 3) {
        const forceMagnitude = (this.size * 3 - distToStall) * 1;
        const distX = this.x > nearestStall.x ? this.x - nearestStall.x - stallsWidth : this.x - nearestStall.x;
        const distY = this.y > nearestStall.y ? this.y - nearestStall.y - stallsHeight : this.y - nearestStall.y;
        avoidanceForceX += forceMagnitude * distX / distToStall;
        avoidanceForceY += forceMagnitude * distY / distToStall;
      }
    }
    
    return [avoidanceForceX, avoidanceForceY];
  }
  
  findNearestStall(stalls) {
    if (!stalls || stalls.length === 0) return null;
    
    let nearestStall = null;
    let shortestDistance = Infinity;
    
    for (const stall of stalls) {
      const distance = stall.calculateDistanceToStall(this.x, this.y);
      if (distance < shortestDistance) {
        shortestDistance = distance;
        nearestStall = stall;
      }
    }
    
    return nearestStall;
  }
  
  draw(ctx) {
    ctx.fillStyle = this.color;
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
    ctx.fill();
  }
}

class Customer extends Human {
  constructor(x, y, speed, productsAmountToBuy, productsCount, money) {
    super(x, y, speed);
    this.money = money * 1000;
    this.direction = Math.PI / 2;
    this.size = 6;
    this.color = '#FF8C00';
    this.uselessProductsCount = 0;
    this.productsToBuy = [];
    this.productsTaken = [];
    this.checkedStallsIDs = [];
    this.checking = false;
    this.state = 'shopping'; // 'shopping', 'checking', 'goingToCashier', 'leaving'
    this.timeSpent = 0;
    this.totalSpent = 0;
    
    for (let i = 0; i < productsAmountToBuy; i++) {
      this.productsToBuy.push(Math.floor(Math.random() * (productsCount - 1) + 1.1));
    }
  }
  
  findNearestStallToCheck(stalls, stallsHeight, stallsWidth) {
    const nearestStall = super.findNearestStall(stalls);
    const nearestStallData = [nearestStall];
    
    if (nearestStall != null && !this.checkedStallsIDs.includes(nearestStall.ID) && !this.checking) {
      if (Math.abs(this.x - nearestStall.x) < Math.abs(this.x - nearestStall.x - stallsWidth)) {
        this.targetX = nearestStall.x - this.size * 2;
      } else {
        this.targetX = nearestStall.x + stallsWidth + this.size * 2;
      }
      nearestStallData.push(this.targetX);
      this.targetY = nearestStall.y + Math.random() * stallsHeight;
      nearestStallData.push(this.targetY);
      return nearestStallData;
    } else if (stalls.length >= 1 && nearestStall != null) {
      let newStalls = stalls.filter(item => item.ID !== nearestStall.ID);
      return this.findNearestStallToCheck(newStalls, stallsHeight, stallsWidth);
    } else {
      return null;
    }
  }
  
  checkStalls(stalls, stallsHeight, stallsWidth, distBetweenStalls, cashiers) {
    if (this.state === 'leaving') return;
    
    if (this.state === 'shopping') {
      const nearestStallData = this.findNearestStallToCheck(stalls, stallsHeight, stallsWidth);
      
      if (!nearestStallData) {
        // No more stalls to check, go to cashier
        this.goToCashier(cashiers);
        return;
      }
      
      const nearestStall = nearestStallData[0];
      
      if (this.y > nearestStall.y && this.y < nearestStall.y + stallsHeight) {
        if (this.targetX < this.x - distBetweenStalls - this.size * 2 || this.targetX > this.x + distBetweenStalls + this.size * 2) {
          this.targetX = this.x;
          this.targetY = Math.abs(this.y - nearestStall.y) < Math.abs(this.y - nearestStall.y - stallsHeight) ? 
            nearestStall.y - this.size * 4 : 
            nearestStall.y + stallsHeight + this.size * 4;
          return;
        }
      }
      
      const distance = calculateDistance(this.x, this.y, nearestStallData[1], nearestStallData[2]);
      
      if (distance < this.size * 2 || this.checking) {
        const productOrder = this.checkForProducts(nearestStall);
        this.checking = true;
        this.state = 'checking';
        
        if (productOrder.length > 0 && productOrder[0] !== null) {
          const product = productOrder[0];
          this.targetX = product.x;
          this.targetY = product.y;
          
          if (calculateDistance(this.x, this.y, product.x, product.y) < this.size * 2) {
            this.takeProduct(productOrder[0]);
            productOrder.shift();
          }
        }
        
        if (productOrder.length === 0) {
          this.checkedStallsIDs.push(nearestStall.ID);
          this.checking = false;
          this.state = 'shopping';
        }
      }
    } else if (this.state === 'goingToCashier') {
      this.timeSpent++;
      const distance = calculateDistance(this.x, this.y, this.targetX, this.targetY);
      if (distance < this.size * 2) {
        this.state = 'inQueue';
      }
    }
  }
  
  goToCashier(cashiers) {
    if (cashiers.length === 0) {
      this.state = 'leaving';
      this.targetX = this.x;
      this.targetY = 700;
      return;
    }
    
    // Find cashier with shortest queue
    let bestCashier = cashiers[0];
    for (const cashier of cashiers) {
      if (cashier.queue.length < bestCashier.queue.length) {
        bestCashier = cashier;
      }
    }
    
    this.targetX = bestCashier.x + bestCashier.width / 2;
    this.targetY = bestCashier.y + bestCashier.height / 2;
    this.state = 'goingToCashier';
    bestCashier.queue.push(this);
  }
  
  takeProduct(product) {
    if (this.money < product.cost) return;
    
    this.productsTaken.push(product);
    this.money -= product.cost;
    this.totalSpent += product.cost;
    
    // Check for attractive nearby products
    for (const productNearby of product.checkNearby()) {
      if (Math.random() * 100 < productNearby.attractive && this.money >= productNearby.cost) {
        this.uselessProductsCount++;
        this.productsTaken.push(productNearby);
        this.money -= productNearby.cost;
        this.totalSpent += productNearby.cost;
        productNearby.stall.products.splice(productNearby.stall.products.indexOf(productNearby), 1);
      }
    }
    
    product.stall.products.splice(product.stall.products.indexOf(product), 1);
  }
  
  checkForProducts(stall) {
    const productsOrder = [];
    for (const product of stall.products) {
      if (this.productsToBuy.includes(product.ID)) {
        productsOrder.push(product);
      }
    }
    productsOrder.sort((a, b) => calculateDistance(this.x, this.y, a.x, a.y) - calculateDistance(this.x, this.y, b.x, b.y));
    return productsOrder;
  }
  
  leaveStore() {
    this.state = 'leaving';
    this.targetX = this.x;
    this.targetY = 700;
  }
  
  draw(ctx) {
    super.draw(ctx);
    
    // Draw basket contents
    ctx.fillStyle = 'rgba(0, 0, 0, 0.5)';
    ctx.beginPath();
    ctx.arc(this.x, this.y - 10, this.productsTaken.length, 0, Math.PI * 2);
    ctx.fill();
  }
}

function calculateDistance(x1, y1, x2, y2) {
  return Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
}

const Simulation = () => {
  const canvasRef = useRef(null);
  const stallsRef = useRef([]);
  const customersRef = useRef([]);
  const cashiersRef = useRef([]);
  const [stats, setStats] = useState({
    totalRevenue: 0,
    customersServed: 0,
    averageSpending: 0,
    uselessProductsSold: 0,
    activeCustomers: 0
  });
  
  const [params, setParams] = useState({
    stallsCount: 5,
    width: 1200,
    height: 675,
    stallsWidth: 20,
    stallsHeight: 425,
    isRunning: true,
    customersCount: 5,
    speed: 1,
    cashiersCount: 2,
    spawnRate: 100
  });
  
  const [visibleCount, setVisibleCount] = useState(0);
  const spawnTimerRef = useRef(0);

  const initSimulation = () => {
    const newStalls = [];
    const newProducts = [];
    const newCustomers = [];
    const newCashiers = [];
    
    const distBetweenStalls = (params.width - params.stallsCount * params.stallsWidth) / (params.stallsCount - 1);
    
    // Create stalls
    for (let i = 0; i < params.stallsCount; i++) {
      const newStall = new Stall(
        i * params.stallsWidth + i * distBetweenStalls,
        50,
        params.stallsWidth,
        params.stallsHeight,
        i + 1
      );
      newStalls.push(newStall);
      
      // Create products for each stall
      for (let j = 0; j < params.stallsHeight / 12; j++) {
        const newProduct = new Product(
          i * params.stallsWidth + i * distBetweenStalls + params.stallsWidth / 2,
          50 + j * 12 + 3,
          Math.random() * 100 + 10,
          (j + 1) + i * (Math.floor(params.stallsHeight / 12) + 1),
          newStall,
          Math.random() <= 0.5 ? 0 : Math.random() * 90
        );
        newStall.products.push(newProduct);
        newProducts.push(newProduct);
      }
    }
    
    // Create cashiers
    const cashierWidth = 30;
    const cashierHeight = 50;
    const cashierSpacing = 50;
    const totalCashiersWidth = params.cashiersCount * cashierWidth + (params.cashiersCount - 1) * cashierSpacing;
    const startX = (params.width - totalCashiersWidth) / 2;
    
    for (let i = 0; i < params.cashiersCount; i++) {
      const newCashier = new Cashier(
        startX + i * (cashierWidth + cashierSpacing),
        params.height - 100,
        cashierWidth,
        cashierHeight
      );
      newCashiers.push(newCashier);
    }
    
    // Create initial customers
    for (let i = 0; i < params.customersCount; i++) {
      newCustomers.push(new Customer(
        Math.random() * params.width,
        params.height - 6,
        params.speed,
        Math.floor(Math.random() * 3) + 1,
        newProducts.length,
        Math.random() * 2 + 1
      ));
    }
    
    customersRef.current = newCustomers;
    stallsRef.current = newStalls;
    cashiersRef.current = newCashiers;
    setVisibleCount(newProducts.length);
    spawnTimerRef.current = 0;
    
    // Reset stats
    setStats({
      totalRevenue: 0,
      customersServed: 0,
      averageSpending: 0,
      uselessProductsSold: 0,
      activeCustomers: newCustomers.length
    });
  };
  
  const handleParamChange = (e) => {
    const { name, value } = e.target;
    setParams(prev => ({
      ...prev,
      [name]: parseFloat(value)
    }));
  };
  
  const toggleSimulation = () => {
    setParams(prev => ({
      ...prev,
      isRunning: !prev.isRunning
    }));
  };
  
  useEffect(() => {
    initSimulation();
  }, [params.stallsCount, params.cashiersCount]);
  
  useEffect(() => {
    if (!canvasRef.current) return;
    
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Restock products
      let restocked = false;
      stallsRef.current.forEach(stall => {
        if (stall.restock(params.stallsHeight / 12)) {
          restocked = true;
        }
      });
      
      if (restocked) {
        let productCount = 0;
        stallsRef.current.forEach(stall => {
          productCount += stall.products.length;
        });
        setVisibleCount(productCount);
      }
      
      // Process cashiers
      cashiersRef.current.forEach(cashier => {
        const finishedCustomer = cashier.processCustomer();
        if (finishedCustomer) {
          finishedCustomer.leaveStore();
          setStats(prev => ({
            ...prev,
            totalRevenue: prev.totalRevenue + finishedCustomer.totalSpent,
            customersServed: prev.customersServed + 1,
            uselessProductsSold: prev.uselessProductsSold + finishedCustomer.uselessProductsCount,
            averageSpending: prev.customersServed > 0 ? 
              (prev.averageSpending * prev.customersServed + finishedCustomer.totalSpent) / (prev.customersServed + 1) : 
              finishedCustomer.totalSpent
          }));
        }
      });
      
      // Move and draw customers
      customersRef.current.forEach(c => {
        c.checkStalls(
          stallsRef.current, 
          params.stallsHeight, 
          params.stallsWidth, 
          (params.width - params.stallsCount * params.stallsWidth) / (params.stallsCount - 1),
          cashiersRef.current
        );
        c.move(canvas.width, canvas.height, stallsRef.current, params.stallsWidth, params.stallsHeight, customersRef.current);
        c.draw(ctx);
      });
      
      // Remove customers who left the store
      customersRef.current = customersRef.current.filter(c => c.y < canvas.height + 20);
      
      // Spawn new customers
      if (params.isRunning) {
        spawnTimerRef.current++;
        if (spawnTimerRef.current > params.spawnRate && customersRef.current.length < 50) {
          const newCustomer = new Customer(
            Math.random() * params.width,
            params.height - 6,
            params.speed,
            Math.floor(Math.random() * 3) + 1,
            stallsRef.current.reduce((acc, stall) => acc + stall.products.length, 0),
            Math.random() * 2 + 1
          );
          customersRef.current.push(newCustomer);
          spawnTimerRef.current = 0;
          
          setStats(prev => ({
            ...prev,
            activeCustomers: customersRef.current.length
          }));
        }
      }
      
      // Draw stalls and products
      stallsRef.current.forEach(p => {
        p.draw(ctx);
        p.products.forEach(pr => pr.draw(ctx));
      });
      
      // Draw cashiers
      cashiersRef.current.forEach(c => c.draw(ctx));
      
      if (params.isRunning) {
        animationFrameId = requestAnimationFrame(animate);
      }
    };
    
    if (params.isRunning) {
      animationFrameId = requestAnimationFrame(animate);
    } else {
      // Draw current state when paused
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      stallsRef.current.forEach(p => p.draw(ctx));
      stallsRef.current.forEach(p => p.products.forEach(pr => pr.draw(ctx)));
      customersRef.current.forEach(c => c.draw(ctx));
      cashiersRef.current.forEach(c => c.draw(ctx));
    }
    
    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [params.isRunning, params.stallsHeight, params.stallsWidth, params.spawnRate]);
  
  return (
    <div className="simulation-container">
      <div className="controls">
        <h2>Управление симуляцией</h2>
        
        <div className="control-group">
          <label>
            Количество отделов:
            <input
              type="range"
              name="stallsCount"
              min="1"
              max="10"
              value={params.stallsCount}
              onChange={handleParamChange}
            />
            {params.stallsCount}
          </label>
        </div>
        
        <div className="control-group">
          <label>
            Количество касс:
            <input
              type="range"
              name="cashiersCount"
              min="1"
              max="5"
              value={params.cashiersCount}
              onChange={handleParamChange}
            />
            {params.cashiersCount}
          </label>
        </div>
        
        <div className="control-group">
          <label>
            Скорость покупателей:
            <input
              type="range"
              name="speed"
              min="0.5"
              max="5"
              step="0.1"
              value={params.speed}
              onChange={handleParamChange}
            />
            {params.speed.toFixed(1)}
          </label>
        </div>
        
        <div className="control-group">
          <label>
            Частота появления:
            <input
              type="range"
              name="spawnRate"
              min="10"
              max="300"
              value={params.spawnRate}
              onChange={handleParamChange}
            />
            {params.spawnRate}
          </label>
        </div>
        
        <div className="buttons">
          <button onClick={initSimulation}>Сбросить</button>
          <button onClick={toggleSimulation}>
            {params.isRunning ? 'Пауза' : 'Старт'}
          </button>
        </div>
        
        <div className="stats">
          <h3>Статистика магазина</h3>
          <p>Выручка: ${stats.totalRevenue.toFixed(2)}</p>
          <p>Обслужено покупателей: {stats.customersServed}</p>
          <p>Средний чек: ${stats.averageSpending.toFixed(2)}</p>
          <p>Ненужных товаров продано: {stats.uselessProductsSold}</p>
          <p>Покупателей в магазине: {stats.activeCustomers}</p>
          <p>Товаров на полках: {visibleCount}</p>
        </div>
      </div>
      
      <div className="simulation-area">
        <canvas
          ref={canvasRef}
          width={params.width}
          height={params.height}
        />
      </div>
    </div>
  );
};

export default Simulation;
